-- ----------------------------
-- Table structure for question
-- ----------------------------
DROP TABLE IF EXISTS `question`;
CREATE TABLE `question` (
  `id` varchar(60) NOT NULL COMMENT '编号',
  `question` varchar(200) default NULL COMMENT '问题',
  `question_desc` varchar(2000) default NULL COMMENT '问题详情',
  `url` varchar(100) default NULL COMMENT '问题链接',
  `author` varchar(60) default NULL COMMENT '问题作者',
  `insert_time` datetime default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

