INSERT INTO `question` VALUES ('cdfb7dd085994023a5aae08502760c54', '为什么毕业三四年之后，同学之间的差距会这么大？', '刚参加完一个大学同学聚会，往日的同学们一个个全都变了样子。当年彼此之间相差并不大，但是现在再看大家简直天差地别，有的连续三年公务员没考上，有的却当了小老板，有的自己创业，有的升任部门总监。大家说话的语气，动作和神态都已经完全跟读书时判若两人，我突然都觉得不认识他们了。怎么会这样？', 'https://www.zhihu.com/question/33971854', '暖石的马涛', '2017-06-10 18:57:47');
INSERT INTO `question` VALUES ('cfa30d159bf44503939405ab5cefd163', '作为一名教师的你，见过的最奇葩的家长是怎样的？', '作为一名教师的你，见过的最奇葩的家长是怎样的？', 'https://www.zhihu.com/question/36482552', '匿名用户', '2017-06-10 18:52:25');

-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` varchar(60) NOT NULL,
  `user_token` varchar(255) default NULL,
  `location` varchar(255) default NULL,
  `business` varchar(255) default NULL,
  `sex` varchar(255) default NULL,
  `employment` varchar(255) default NULL,
  `education` varchar(255) default NULL,
  `username` varchar(255) default NULL,
  `url` varchar(255) default NULL,
  `agrees` int(11) default NULL,
  `thanks` int(11) default NULL,
  `asks` int(11) default NULL,
  `answers` int(11) default NULL,
  `posts` int(11) default NULL,
  `followees` int(11) default NULL,
  `followers` int(11) default NULL,
  `hashId` varchar(255) default NULL,
  `insert_time` datetime default NULL,
  PRIMARY KEY  (`id`),
  KEY `id` (`id`),
  KEY `index_user_token` (`user_token`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

